select count(t1.create_time) total,t2.data onenum,t3.data sevennum from
api_event_entry t1,
(select totb.today-yestb.yesterday data from(select count(1) yesterday from api_event_entry where  create_time >= DATE_SUB(CURDATE(),INTERVAL 1 day) and create_time < curdate()) yestb , (select count(1)today from api_event_entry where  create_time >= CURDATE() ) totb
)t2,
( select t.data-y.data data from (select count(1) data from api_event_entry where create_time >= DATE_SUB(curdate(),INTERVAL 6 DAY)) t ,(select count(1) data from api_event_entry where create_time  < date_sub(curdate(),INTERVAL 7 DAY) and  create_time >= DATE_SUB(curdate(),INTERVAL 13 DAY))y
) t3

select
concat((@rowNum:=@rowNum+1),'')
AS id,
s.* from
( select de.channel,DATE_FORMAT(de.create_time,'%Y-%m-%d %H:%i:%s')
createTime,
(case de.sys_status
when 0 then '-'
when 1 then '通过'
when 2 then
(case de.man_status
when 0 then '人工审核'
when 1 then '通过'
when 2 then '拒绝'
end
)
when 3 then '拒绝'
when 4 then '失效'
end
)
sysStatus,
(case de.type
when 0 then '正常进件'
when 1 then '测试'
end
)
type,de.user_name userName,de.mobile,de.id_card idCard,ebt.type_name businessId,es.name senceId,de.rules_name
rulesName, de.approver_name approverName
from api_event_entry de
left JOIN engine_business_type ebt ON de.business_id=ebt.id
left JOIN engine_scenes es ON de.sence_id=es.id
WHERE
1=1

    and de.channel = #{channel}


    and de.remark = #{remark}


    and de.business_id = #{businessId}


    and de.sence_id = #{senceId}


    and de.rules_id = #{rulesId}


    and de.user_id = #{userId}


    and de.user_name = #{userName}


    and de.id_card = #{idCard}


    and de.mobile = #{mobile}


    and de.sys_status = #{sysStatus}


    and de.man_status = #{manStatus}


    and de.status = #{status}


    and de.create_time = #{createTime}


    AND de.create_time >= #{startTime}


    AND de.create_time <= #{endTime}

order by createTime) s,(Select (@rowNum :=0) ) b

select concat((@rowNum:=@rowNum+1),'') AS id, s.* from (select de.channel,DATE_FORMAT(de.create_time,'%Y-%m-%d %H:%i:%s') createTime, (case de.sys_status when 0 then '-' when 1 then '通过' when 2 then (case de.man_status when 0 then '人工审核' when 1 then '通过' when 2 then '拒绝' end ) when 3 then '拒绝' when 4 then '失效' end ) sysStatus, (case de.type when 0 then '正常进件' when 1 then '测试' end ) type,de.user_name userName,de.mobile,de.id_card idCard,ebt.type_name businessId,es.name senceId,de.rules_name rulesName, de.approver_name approverName from api_event_entry de left JOIN engine_business_type ebt ON de.business_id=ebt.id left JOIN engine_scenes es ON de.sence_id=es.id WHERE 1=1 and de.id in
#{item}
order by createTime) s,(Select (@rowNum :=0) ) b

SELECT

    date_format(create_time,'%Y-%m-%d %H:00:00') as create_time,


    date_format(create_time,'%Y-%m-%d') as create_time,


    date_format(create_time,'%Y-%m') as create_time,

 count(id) as count FROM api_event_entry where
user_id = #{userId}
and sys_status != 0

    AND sys_status = #{sysStatus}


    AND channel = #{channel}


    AND sence_id = #{sceneId}

and create_time >=DATE_SUB(CURDATE(), INTERVAL #{param} ${type})

    and create_time BETWEEN #{startTime} and #{endTime}


    and rules_id = #{rulesId}

group by ${type}(create_time)

select  t.create_time ,sys_status, sum(t.count) as total from (
select sys_status, count(id) as count,

    date_format(create_time,'%Y-%m-%d %H:00:00') as create_time


    date_format(create_time,'%Y-%m-%d') as create_time


    date_format(create_time,'%Y-%m') as create_time

from api_event_entry where sys_status != 0 and  create_time >=DATE_SUB(CURDATE(), INTERVAL #{param} ${type})

    AND sys_status = #{sysStatus}


    AND channel = #{channel}


    AND sence_id = #{sceneId}


    and create_time BETWEEN #{startTime} and #{endTime}


    and rules_id = #{rulesId}

group by create_time, sys_status
) t GROUP BY t.create_time, sys_status;

select  t.create_time ,t.expend_level, sum(t.count) as total from (
select(
CASE
WHEN expend_time  <=  500 THEN 1
WHEN expend_time > 500  and expend_time  <=  1000 THEN 2
WHEN expend_time > 1000  and expend_time  <=  2000 THEN 3
WHEN expend_time > 2000  and expend_time  <=  5000 THEN 4
WHEN expend_time > 5000  and expend_time  <=  10000 THEN 5
ELSE 6 END
) as expend_level,
count(id) as count,

    date_format(create_time,'%Y-%m-%d %H:00:00') as create_time


    date_format(create_time,'%Y-%m-%d') as create_time

from api_event_entry
where
user_id = #{userId}

    AND sys_status = #{sysStatus}


    AND channel = #{channel}


    AND sence_id = #{sceneId}


    AND create_time BETWEEN #{startTime} AND #{endTime}

and create_time >=DATE_SUB(CURDATE(), INTERVAL #{param} ${type})  group by create_time, expend_level
) t GROUP BY t.create_time, t.expend_level

select t.create_time ,t.score_level, sum(t.count) as total from ( select( CASE WHEN score <= 50 THEN 1 WHEN score > 50 and score <= 100 THEN 2 WHEN score > 100 and score <= 150 THEN 3 WHEN score > 150 and score <= 200 THEN 4 WHEN score > 200 and score <= 300 THEN 5 ELSE 6 END ) as score_level, count(id) as count,
date_format(create_time,'%Y-%m-%d %H:00:00') as create_time
date_format(create_time,'%Y-%m-%d') as create_time
from api_event_entry where user_id = #{userId} and score != "null"
AND sys_status = #{sysStatus}
AND channel = #{channel}
AND sence_id = #{sceneId}
AND create_time BETWEEN #{startTime} AND #{endTime}
and create_time >=DATE_SUB(CURDATE(), INTERVAL #{param} ${type}) group by create_time, score_level ) t GROUP BY t.create_time, t.score_level

SET @row_num = 0;
