FROM api_event_entry

    user_id = #{userId}
    
        AND sys_status = #{sysStatus}
    
    
        AND channel = #{channel}
    
    
        AND sence_id = #{sceneId}
    
    
        AND create_time BETWEEN #{startTime} AND #{endTime}
    
    
